import React, { useEffect, useRef, useState } from 'react';

const CardStack = () => {
  const [visibleCards, setVisibleCards] = useState(0);
  const sectionRef = useRef(null);

  const cardsData = [
    {
      id: 1,
      title: "Lorem",
      subtitle: "Ipsum",
      backgroundColor: "#E5E5E5",
      textColor: "#000",
      zIndex: 3,
      offset: 0
    },
    {
      id: 2,
      title: "Lorem",
      subtitle: "Ipsum", 
      backgroundColor: "#B8B8B8",
      textColor: "#000",
      zIndex: 2,
      offset: 20
    },
    {
      id: 3,
      title: "Lorem",
      subtitle: "Ipsum",
      backgroundColor: "#1D1D1D",
      textColor: "#fff",
      zIndex: 1,
      offset: 40
    }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          // Show cards one by one with delay
          setTimeout(() => setVisibleCards(1), 0);
          setTimeout(() => setVisibleCards(2), 500);
          setTimeout(() => setVisibleCards(3), 1000);
        }
      },
      { threshold: 0.3 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  return (
    <div className="cardstack-section" ref={sectionRef}>
      <div className="container">
        <h2>
          Where Can I Get Some?
        </h2>
        
        <div className="stacked-cards-container">
          {cardsData.map((card, index) => (
            <div 
              key={card.id}
              className={`stacked-card card-${card.id} ${visibleCards >= card.id ? 'visible' : ''}`}
              style={{
                backgroundColor: card.backgroundColor,
                zIndex: card.zIndex,
                top: card.offset,
                color: card.textColor
              }}
            >
              <div className="card-content">
                <h3>{card.title}</h3>
                <h3>{card.subtitle}</h3>
              </div>
            </div>
          ))}
        </div>
        
        <div className='main-card-holder'>
        <div className="card-container">
          <div className="card">
            <div className="card-header">
              <h3>Lorem</h3>
              <h3>Ipsum</h3>
            </div>  
            <div className='white-box-container'>
              <div className='white-box'></div>
              <div>
                <div className='white-box-text'>
                  <h1>Where can I</h1> 
                  <h1> get some?</h1>
                  <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. </p>
                  </div>
                
              </div>
            </div>
            <div className='white-box-container-two'>
                <div className='white-box-text-two'>
                <p> Artists from across India were invited—each a specialist in their region's distinctive art form. They were asked to celebrate the sun through their unique visual language. The results were bold and intricate, curvy and straight, colorful and graceful—but above all, they were a joyful tribute to the sun.</p>
                  </div>
               
             
              <div className='white-box-two'></div>
            </div>
          
          </div>
        </div>
        </div>
      </div>
    </div>
  );
};

export default CardStack;
