import React from 'react';

const NeonCard = ({ 
  title, 
  description, 
  className = "",
  color = "#00ff88",
  width = 308.9552254705535,
  height = 493.99999161783234,
  top = 390,
  left = 318,
  angle = -11.78,
  opacity = 1
}) => {
  const cardStyle = {
    backgroundColor: color,
    width: `${width}px`,
    height: `${height}px`,
    top: `${top}px`,
    left: `${left}px`,
    transform: `rotate(${angle}deg)`,
    opacity: opacity,
    position: 'absolute'
  };

  return (
    <div className={`neon-card ${className}`} style={cardStyle}>
      <div className="card-content">
        <h3 className="card-title">{title}</h3>
        <p className="card-description">
          {description}
        </p>
      </div>
    </div>
  );
};

export default NeonCard;
