import React, { useEffect, useRef, useState } from 'react';
import { ImageAssets } from '../constants/imageassets';

const WaveSection = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [pills, setPills] = useState([]);
  const sectionRef = useRef(null);
  const animationRef = useRef(null);

  // Physics constants
  const GRAVITY = 0.3;
  const DAMPING = 0.7;
  const BOUNCE = 0.6;

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
          startPillAnimation();
        }
      },
      { 
        threshold: 0.1,
        rootMargin: '0px 0px -100px 0px'
      }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, [isVisible]);

  const startPillAnimation = () => {
    const pillData = [
      { id: 1, x: 10, y: -100, vx: 0, vy: 0, text: 'Lorem ipsum' },
      { id: 2, x: 30, y: -100, vx: 0, vy: 0, text: 'Dolor sit amet' },
      { id: 3, x: 50, y: -100, vx: 0, vy: 0, text: 'Consectetur' },
      { id: 4, x: 70, y: -100, vx: 0, vy: 0, text: 'Adipiscing elit' },
      { id: 5, x: 15, y: -100, vx: 0, vy: 0, text: 'Sed do eiusmod' },
      { id: 6, x: 40, y: -100, vx: 0, vy: 0, text: 'Tempor incididunt' },
      { id: 7, x: 65, y: -100, vx: 0, vy: 0, text: 'Ut labore' },
      { id: 8, x: 85, y: -100, vx: 0, vy: 0, text: 'Dolore magna' }
    ];
    
    setPills(pillData);
    animatePills(pillData);
  };

  const animatePills = (initialPills) => {
    let currentPills = [...initialPills];
    const containerHeight = 400; // Height of pill-img-container
    
    const animate = () => {
      currentPills = currentPills.map(pill => {
        // Apply gravity
        pill.vy += GRAVITY;
        
        // Update position
        pill.y += pill.vy;
        
        // Bounce off bottom
        if (pill.y > containerHeight - 60) {
          pill.y = containerHeight - 60;
          pill.vy *= -BOUNCE;
          
          // Add some horizontal movement for realism
          pill.vx += (Math.random() - 0.5) * 0.5;
        }
        
        // Apply horizontal velocity
        pill.x += pill.vx;
        
        // Bounce off sides
        if (pill.x < 5) {
          pill.x = 5;
          pill.vx *= -DAMPING;
        } else if (pill.x > 95) {
          pill.x = 95;
          pill.vx *= -DAMPING;
        }
        
        // Apply damping
        pill.vx *= 0.99;
        pill.vy *= 0.99;
        
        return pill;
      });
      
      setPills([...currentPills]);
      
      // Continue animation
      if (currentPills.some(pill => pill.y < containerHeight - 60 || Math.abs(pill.vy) > 0.1)) {
        animationRef.current = requestAnimationFrame(animate);
      }
    };
    
    animate();
  };

  return (
    <div className="wave-section" ref={sectionRef}>
      <div className="container">
        <div className="wave-content">
          <h2>Where Does It Come From?</h2>
          <p>
          Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, 
          </p>
          <div className='pill-img-container' style={{ backgroundImage: `url(${ImageAssets.waveimg})`, height: '400px', backgroundSize: 'cover', backgroundPosition: 'center' }}>
            <div className="pills-container">
              {pills.map(pill => (
                <div 
                  key={pill.id}
                  className="pill"
                  style={{
                    position: 'absolute',
                    left: `${pill.x}%`,
                    top: `${pill.y}px`,
                    transform: 'translateX(-50%)',
                    opacity: pill.y > -50 ? 1 : 0
                  }}
                >
                  {pill.text}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WaveSection;
