// Color constants for the entire project
export const COLORS = {
  // Primary colors
  NEON_GREEN: '#00ff88',
  NEON_GREEN_DARK: '#00cc6a',
  
  // New neon card colors
  NEON_BRIGHT_GREEN: '#39FF14',
  NEON_LIME_GREEN: '#B6FF00',
  
  // Background colors
  BLACK: '#000000',
  DARK_GRAY: '#333333',
  GRAY: '#666666',
  LIGHT_GRAY: '#999999',
  
  // Text colors
  WHITE: '#F2F2F2',
  TEXT_PRIMARY: '#ffffff',
  TEXT_SECONDARY: '#cccccc',
  TEXT_DARK: '#0B0B0B',
  
  // Accent colors
  PURPLE: '#8b5cf6',
  PURPLE_LIGHT: '#a855f7',
  PINK: '#ec4899',
  
  // Silver/Metal colors
  SILVER: '#c0c0c0',
  SILVER_LIGHT: '#e0e0e0',
  
  // Transparent colors
  NEON_GREEN_TRANSPARENT: 'rgba(0, 255, 136, 0.1)',
  NEON_GREEN_GLOW: 'rgba(0, 255, 136, 0.3)',
  NEON_GREEN_GLOW_STRONG: 'rgba(0, 255, 136, 0.8)',
  WHITE_TRANSPARENT: 'rgba(255, 255, 255, 0.2)',
  WHITE_TRANSPARENT_LIGHT: 'rgba(255, 255, 255, 0.1)',
  BLACK_TRANSPARENT: 'rgba(0, 0, 0, 0.9)',
};

// Typography constants
export const TYPOGRAPHY = {
  FONT_FAMILY: {
    PRIMARY: 'Sora, -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif',
  },
  FONT_WEIGHTS: {
    BOLD: 700,
    MEDIUM: 500,
    NORMAL: 400,
  },
  FONT_SIZES: {
    HERO: '250px',
    LARGE: '4rem',
    MEDIUM: '2.5rem',
    NORMAL: '1.2rem',
    SMALL: '0.9rem',
  },
  LINE_HEIGHTS: {
    TIGHT: '100%',
    NORMAL: '1.6',
  },
  LETTER_SPACING: {
    WIDE: '3px',
    NORMAL: '1px',
    NONE: '0%',
  },
};
