import React, { useState, useEffect, useRef } from 'react';
import SEO from '../components/SEO';
import MolecularStructure from '../components/MolecularStructure';
import WaveSection from '../components/WaveSection';
import CardStack from '../components/CardStack';
import NeonCardsContainer from '../components/NeonCardsContainer';
import { COLORS } from '../constants/colors';
import { ImageAssets } from '../constants/imageassets';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

const Home = () => {
  const [scrollY, setScrollY] = useState(0);
  const [showCards, setShowCards] = useState(false);
  const neonSectionRef = useRef(null);
  const neonContentRef = useRef(null);
  const neonImageRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);

      // Show cards earlier in the scroll (around 150px scroll)
      if (window.scrollY > 150) {
        setShowCards(true);
      } else {
        setShowCards(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // GSAP ScrollTrigger animation for neon section
  useEffect(() => {
    console.clear();

    // Register GSAP plugins
    gsap.registerPlugin(ScrollTrigger);

    const neonSection = neonSectionRef.current;
    const neonContent = neonContentRef.current;
    const neonImage = neonImageRef.current;

    if (!neonSection || !neonContent || !neonImage) return;

    // Set initial states
    gsap.set(neonContent, { opacity: 0, scale: 0.8 });
    gsap.set(neonImage, { scale: 1, z: 0 });

    // Create the scroll trigger animation
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: neonSection,
        start: "top top",
        end: "+=200%",
        pin: true,
        scrub: 1,
        markers: true
      }
    });

    // First phase: Image scales up and moves forward
    tl.to(neonImage, {
      scale: 2.5,
      z: 400,
      transformOrigin: "center center",
      ease: "power1.inOut",
      duration: 0.5
    })
    // Second phase: Content fades in and scales up inside the image
    .to(neonContent, {
      opacity: 1,
      scale: 1,
      transformOrigin: "center center",
      ease: "power1.inOut",
      duration: 0.3
    }, "-=0.2")
    // Third phase: Image scales down slightly to show content better
    .to(neonImage, {
      scale: 2,
      z: 300,
      transformOrigin: "center center",
      ease: "power1.inOut",
      duration: 0.2
    });

    // Cleanup function
    return () => {
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
    };
  }, []);

  const structuredData = {
    "@context": "https://schema.org",
    "@type": "WebSite",
    "name": "React SEO Website",
    "url": "https://yourwebsite.com",
    "description": "Modern React website with SEO optimization",
    "potentialAction": {
      "@type": "SearchAction",
      "target": "https://yourwebsite.com/search?q={search_term_string}",
      "query-input": "required name=search_term_string"
    }
  };

  const neonCardsData = [
    {
      title: "Lorem Ipsum",
      description: "Lorem ipsum is simply dummy text of the printing and typesetting industry",
      color: COLORS.NEON_BRIGHT_GREEN,
      width: 308.9552254705535,
      height: 493.99999161783234,
      top: -45,
      left: 252,
      angle: 12,
      opacity: 1
    },
    {
      title: "Lorem Ipsum",
      description: "Lorem ipsum is simply dummy text of the printing and typesetting industry",
      color: COLORS.NEON_LIME_GREEN,
      width: 308.9552254705535,
      height: 493.99999161783234,
      top: -45,
      left: 720,
      angle: -22,
      opacity: 1
    }
  ];

  return (
    <>
      <SEO
        title="Home"
        description="Welcome to our modern React website with advanced SEO optimization. Discover our services and learn more about our company."
        keywords="react, seo, website, modern, optimization"
        url="/"
        structuredData={structuredData}
      />

      {/* Hero Section with Neon Cards */}
      <div className="hero-section">
        <div className="hero-content">
          <h1 
            className="hero-title-first"
            style={{
              fontSize: showCards ? '14rem' : Math.max(14, 20 - scrollY * 0.003) + 'rem',
              transform: `translateY(${scrollY * 0.2}px)`,
              transition: showCards ? 'font-size 2.5s cubic-bezier(0.16, 1, 0.3, 1), transform 0.8s cubic-bezier(0.16, 1, 0.3, 1)' : 'font-size 1.2s cubic-bezier(0.23, 1, 0.32, 1), transform 0.6s cubic-bezier(0.23, 1, 0.32, 1)'
            }}
          >
            LOREM
          </h1>
          <h1 
            className="hero-title-second"
            style={{
              fontSize: showCards ? '12rem' : Math.max(12, 18 - scrollY * 0.003) + 'rem',
              transform: `translateY(${scrollY * 0.2}px)`,
              transition: showCards ? 'font-size 2.5s cubic-bezier(0.16, 1, 0.3, 1), transform 0.8s cubic-bezier(0.16, 1, 0.3, 1)' : 'font-size 1.2s cubic-bezier(0.23, 1, 0.32, 1), transform 0.6s cubic-bezier(0.23, 1, 0.32, 1)'
            }}
          >
            IPSUM LORE
          </h1>

          <div className="latest-text">
            Latest celebrated
          </div>

          <div 
            className={`neon-cards ${showCards ? 'cards-visible' : 'cards-hidden'}`}
            style={{
              opacity: showCards ? 1 : 0,
              transform: showCards ? 'translateY(0) scale(1)' : 'translateY(30px) scale(0.95)',
              transition: 'all 1.2s cubic-bezier(0.25, 0.46, 0.45, 0.94)'
            }}
          >
            <NeonCardsContainer cards={neonCardsData} />
          </div>

          {/* <MolecularStructure /> */}
        </div>
      </div>

      {/* Solid Neon Section with Scroll Animation */}
      <div className="neon-section" ref={neonSectionRef}>
        <div className="neon-wrapper">
          <div className="neon-content" ref={neonContentRef}>
            <div className="container">
              <div className="title-group">
                <h2>Lorem.</h2>
                <h2>Ipsum.</h2>
                <h2>Lorem.</h2>
              </div>
              <p>
              Lorem Ipsum is simply dummy text of the printing and typesetting industry.Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum is simply dummy text of the printing and typesetting industry.
              </p>
            </div>
          </div>
          <div className="neon-image-container">
            <img 
              src={ImageAssets.circleimg} 
              alt="Circle Image"
              ref={neonImageRef}
            />
          </div>
        </div>
      </div>

      {/* Circle Section */}
      <div className="circle-section">
        <div className="container">
          <div className="circle-container">
            <div className="circle"></div>
            <div className="circle-texts">
              <div className="circle-text">Lorem. Ipsum.</div>
              <div className="circle-text">Lorem. Ipsum.</div>
              <div className="circle-text">Lorem. Ipsum.</div>
              <div className="circle-text">Lorem. Ipsum.</div>
              <div className="circle-text">Lorem. Ipsum.</div>
            </div>
          </div>
        </div>
      </div>

      {/* Wave Section */}
      <WaveSection />

      {/* Card Stack Section */}
      <CardStack />
    </>
  );
};

export default Home;
