import React, { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

const CardStack = () => {
  const [visibleCards, setVisibleCards] = useState(0);
  const sectionRef = useRef(null);
  const card1Ref = useRef(null);
  const card2Ref = useRef(null);
  const card3Ref = useRef(null);

  const cardsData = [
    { id: 1, title: "Lorem", subtitle: "Ipsum", bg: "#1D1D1D", textColor: "#fff", ref: card1Ref },
    { id: 2, title: "Lorem", subtitle: "Ipsum", bg: "#B8B8B8", textColor: "#000", ref: card2Ref },
    { id: 3, title: "Lorem", subtitle: "Ipsum", bg: "#E5E5E5", textColor: "#000", ref: card3Ref }
  ];

  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);

    // Set initial positions - all cards start from bottom
    gsap.set([card1Ref.current, card2Ref.current, card3Ref.current], {
      y: "100vh",
      opacity: 1
    });

    // Card 1 animation
    gsap.to(card1Ref.current, {
      y: 0,
      duration: 1,
      ease: "power2.out",
      scrollTrigger: {
        trigger: sectionRef.current,
        start: "top center",
        end: "center center",
        scrub: 1,
        onUpdate: (self) => {
          if (self.progress > 0.3) {
            setVisibleCards(1);
          }
        }
      }
    });

    // Card 2 animation
    gsap.to(card2Ref.current, {
      y: 0,
      duration: 1,
      ease: "power2.out",
      scrollTrigger: {
        trigger: sectionRef.current,
        start: "center center",
        end: "bottom center",
        scrub: 1,
        onUpdate: (self) => {
          if (self.progress > 0.3) {
            setVisibleCards(2);
          }
        }
      }
    });

    // Card 3 animation
    gsap.to(card3Ref.current, {
      y: 0,
      duration: 1,
      ease: "power2.out",
      scrollTrigger: {
        trigger: sectionRef.current,
        start: "bottom center",
        end: "bottom top",
        scrub: 1,
        onUpdate: (self) => {
          if (self.progress > 0.3) {
            setVisibleCards(3);
          }
        }
      }
    });

    return () => {
      ScrollTrigger.getAll().forEach(trigger => {
        if (trigger.trigger === sectionRef.current) {
          trigger.kill();
        }
      });
    };
  }, []);

  return (
    <div className="cardstack-section" ref={sectionRef}>
      <div className="container">
        <h2>Where Can I Get Some?</h2>
        
        <div className="stacked-cards-container">
          {cardsData.map((card, index) => (
            <div 
              key={card.id} 
              ref={card.ref}
              className={`stacked-card card-${card.id} ${visibleCards >= card.id ? 'visible' : ''}`}
              style={{
                backgroundColor: card.bg,
                color: card.textColor,
                zIndex: 4 - card.id,
                top: `${index * 20}px`,
                left: '50%',
                transform: 'translateX(-50%)'
              }}
            >
              <div className="card-content">
                {visibleCards >= card.id ? (
                  <>
                    <div className="card-header">
                      <h3>{card.title}</h3>
                      <h3>{card.subtitle}</h3>
                    </div>
                    
                    <div className="card-body">
                      <div className='white-box-container'>
                        <div className='white-box'></div>
                        <div>
                          <div className='white-box-text'>
                            <h1>Where can I</h1> 
                            <h1> get some?</h1>
                            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. </p>
                          </div>
                        </div>
                      </div>
                      <div className='white-box-container-two'>
                        <div className='white-box-text-two'>
                          <p> Artists from across India were invited—each a specialist in their region's distinctive art form. They were asked to celebrate the sun through their unique visual language. The results were bold and intricate, curvy and straight, colorful and graceful—but above all, they were a joyful tribute to the sun.</p>
                        </div>
                        <div className='white-box-two'></div>
                      </div>
                    </div>
                  </>
                ) : (
                  <div className="card-header collapsed">
                    <h3>{card.title}</h3>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CardStack;
