import React, { useEffect, useRef, useState } from 'react';

const CardStack = () => {
  const [visibleCards, setVisibleCards] = useState(0);
  const sectionRef = useRef(null);
  const card1Ref = useRef(null);
  const card2Ref = useRef(null);
  const card3Ref = useRef(null);

  const cardsData = [
    { id: 1, title: "Lorem", subtitle: "Ipsum", bg: "#1D1D1D", textColor: "#fff", ref: card1Ref },
    { id: 2, title: "Lorem", subtitle: "Ipsum", bg: "#B8B8B8", textColor: "#000", ref: card2Ref },
    { id: 3, title: "Lorem", subtitle: "Ipsum", bg: "#E5E5E5", textColor: "#000", ref: card3Ref }
  ];

  useEffect(() => {
    const handleScroll = () => {
      const section = sectionRef.current;
      if (!section) return;

      const rect = section.getBoundingClientRect();
      const windowHeight = window.innerHeight;
      
      // Card 1 appears when section is 80% visible
      if (rect.top < windowHeight * 0.8 && rect.bottom > windowHeight * 0.2) {
        setVisibleCards(1);
        if (card1Ref.current) {
          card1Ref.current.style.transform = 'translateX(-50%) translateY(0)';
        }
      }
      
      // Card 2 appears when section is 60% visible
      if (rect.top < windowHeight * 0.6 && rect.bottom > windowHeight * 0.4) {
        setVisibleCards(2);
        if (card2Ref.current) {
          card2Ref.current.style.transform = 'translateX(-50%) translateY(0)';
        }
      }
      
      // Card 3 appears when section is 40% visible
      if (rect.top < windowHeight * 0.4 && rect.bottom > windowHeight * 0.6) {
        setVisibleCards(3);
        if (card3Ref.current) {
          card3Ref.current.style.transform = 'translateX(-50%) translateY(0)';
        }
      }
    };

    // Set initial positions
    if (card1Ref.current) {
      card1Ref.current.style.transform = 'translateX(-50%) translateY(100vh)';
    }
    if (card2Ref.current) {
      card2Ref.current.style.transform = 'translateX(-50%) translateY(100vh)';
    }
    if (card3Ref.current) {
      card3Ref.current.style.transform = 'translateX(-50%) translateY(100vh)';
    }

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial call

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="cardstack-section" ref={sectionRef}>
      <div className="container">
        <h2>Where Can I Get Some?</h2>
        
        <div className="stacked-cards-container">
          {cardsData.map((card, index) => (
            <div 
              key={card.id} 
              ref={card.ref}
              className={`stacked-card card-${card.id} ${visibleCards >= card.id ? 'visible' : ''}`}
              style={{
                backgroundColor: card.bg,
                color: card.textColor,
                zIndex: 4 - card.id,
                top: `${index * 20}px`,
                left: '50%',
                transform: 'translateX(-50%)'
              }}
            >
              <div className="card-content">
                {visibleCards >= card.id ? (
                  <>
                    <div className="card-header">
                      <h3>{card.title}</h3>
                      <h3>{card.subtitle}</h3>
                    </div>
                    
                    <div className="card-body">
                      <div className='white-box-container'>
                        <div className='white-box'></div>
                        <div>
                          <div className='white-box-text'>
                            <h1>Where can I</h1> 
                            <h1> get some?</h1>
                            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. </p>
                          </div>
                        </div>
                      </div>
                      <div className='white-box-container-two'>
                        <div className='white-box-text-two'>
                          <p> Artists from across India were invited—each a specialist in their region's distinctive art form. They were asked to celebrate the sun through their unique visual language. The results were bold and intricate, curvy and straight, colorful and graceful—but above all, they were a joyful tribute to the sun.</p>
                        </div>
                        <div className='white-box-two'></div>
                      </div>
                    </div>
                  </>
                ) : (
                  <div className="card-header collapsed">
                    <h3>{card.title}</h3>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CardStack;
