import React, { useEffect, useRef, useState } from 'react';

const CardStack = () => {
  const [visibleCards, setVisibleCards] = useState(0);
  const sectionRef = useRef(null);

  const cardsData = [
    { id: 1, title: "Lorem", subtitle: "Ipsum", bg: "#1D1D1D", textColor: "#fff" },
    { id: 2, title: "Lorem", subtitle: "Ipsum", bg: "#B8B8B8", textColor: "#000" },
    { id: 3, title: "Lorem", subtitle: "Ipsum", bg: "#E5E5E5", textColor: "#000" }
  ];

  useEffect(() => {
    const handleScroll = () => {
      const section = sectionRef.current;
      if (!section) return;

      const rect = section.getBoundingClientRect();
      const windowHeight = window.innerHeight;
      const sectionHeight = rect.height;
      
      // Calculate scroll progress within the section
      const scrollProgress = Math.max(0, Math.min(1, (windowHeight - rect.top) / (windowHeight + sectionHeight)));
      
      // Card 1 appears when section starts coming into view
      if (scrollProgress > 0.1) {
        setVisibleCards(1);
      }
      
      // Card 2 starts sliding up when section is 30% visible
      if (scrollProgress > 0.3) {
        setVisibleCards(2);
      }
      
      // Card 3 starts sliding up when section is 60% visible
      if (scrollProgress > 0.6) {
        setVisibleCards(3);
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial call

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="cardstack-section" ref={sectionRef}>
      <div className="container">
        <h2>Where Can I Get Some?</h2>
        
        <div className="stacked-cards-container">
          {cardsData.map((card, index) => (
            <div 
              key={card.id} 
              className={`stacked-card card-${card.id} ${visibleCards >= card.id ? 'visible' : ''}`}
              style={{
                backgroundColor: card.bg,
                color: card.textColor,
                zIndex: 4 - card.id,
                top: `${index * 20}px`,
                left: '50%',
                transform: 'translateX(-50%)'
              }}
            >
              <div className="card-content">
                {visibleCards >= card.id ? (
                  <>
                    <div className="card-header">
                      <h3>{card.title}</h3>
                      <h3>{card.subtitle}</h3>
                    </div>
                    
                    <div className="card-body">
                      <div className='white-box-container'>
                        <div className='white-box'></div>
                        <div>
                          <div className='white-box-text'>
                            <h1>Where can I</h1> 
                            <h1> get some?</h1>
                            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. </p>
                          </div>
                        </div>
                      </div>
                      <div className='white-box-container-two'>
                        <div className='white-box-text-two'>
                          <p> Artists from across India were invited—each a specialist in their region's distinctive art form. They were asked to celebrate the sun through their unique visual language. The results were bold and intricate, curvy and straight, colorful and graceful—but above all, they were a joyful tribute to the sun.</p>
                        </div>
                        <div className='white-box-two'></div>
                      </div>
                    </div>
                  </>
                ) : (
                  <div className="card-header collapsed">
                    <h3>{card.title}</h3>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CardStack;
